if (msg.topic === "setup") {
    context.set("steps", msg.payload);
    context.set("current", 0);
    return msg;
}

if (context.get("current") === undefined) {
    return;
}

let steps = context.get("steps") ?? [];
let current = context.get("current") ?? 0;

if (msg.topic === "step") {
    if (current > steps.length) {
        current = 0;
        for (let i = 0; i<steps.length; i++) {
            steps[i].color = "grey";
        }
    } else {
        if (current > 0) {
            if (current === 2) {
                steps[current-1].color = "red";
            } else {
                steps[current-1].color = "green";
            }
        }
        if (current < steps.length) {
            steps[current].color = "yellow";
        }
    }
    current++;
}


context.set("current", current);
context.set("steps", steps);
msg.payload = steps;
return msg;
