targets.query:
from(bucket: "nodered")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "solar")
  |> filter(fn: (r) => r["inverter"] == "garage")
  |> filter(fn: (r) => contains(value: r["_field"], set: ["pv2current","pv2voltage","solarpower","pv2power","outputpower","gridfrequency","gridvoltage","tempinverter","tempipm"]))
